.seprate-contrent-text small {
    font-family: 'Roboto Flex';
    font-size: 28px;
    color: #ffffff;
    display: block;
    margin-bottom: 8px;
    text-transform: uppercase;
    text-align: center;
}
.seprate-contrent-text h1 {
    font-family: 'proxima_novaextrabold';
    font-size: 80px;
    color: #ffffff;
    display: block;
    margin: 0px;
    text-align: center;
    margin-right: 200px;
    margin-left: 200px;
}
.seprate-contrent-text p {
    font-family: 'Roboto Flex';
    font-size: 26px;
    line-height: 38px;
    color: #ffffff;
    display: block;
    margin-bottom: 15px;
    text-align: center;
    margin-right: 200px;
    margin-left: 200px;
}
.seprate-contrent-text hr {
    border-top: 1px solid #fff;
    width: 62px;
    display: block;
    margin: 30px 674px 43px;
    text-align: center;
}
.seprate-contrent-text {
    max-width: 100%;
}
.meddmax-about-info {
    max-width: 1410px;
    width: 92%;
    display: -webkit-flex;
    align-items: flex-end;
    justify-content: space-between;
    position: relative;
}
.meddmax-imagery-section {
    width: 35.4%;
    height: 630px;
    display: inline-block;
    margin: 0px;
    -webkit-box-shadow: 0px 18px 30px rgba(0,0,0,.15);
    box-shadow: 0px 18px 30px rgba(0,0,0,.15);
}
.meddmax-imagery-section img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
}
.row {
    flex-wrap: wrap;
    margin-right: -15px;
    margin-left: -15px;
}
.meddmax-content-info {
    width: 58.6%;
    display: inline-block;
    margin: 0px;
    position: relative;
    padding-bottom: 70px;
}
.meddmax-content-info p {
    font-family: 'Roboto Flex';
    font-size: 26px;
    line-height: 38px;
    color: #000000;
    display: block;
    margin-bottom: 28px;
}
.meddmax-about-info::after {
    content: "";
    position: absolute;
    left: 576px;
    right: 0;
    bottom: 0;
    width: 58%;
    height: 6px;
    background-color: #03a7e0;
    margin: 0px auto;
    display: block;
}
.instagram-section-bg {
    width: 100%;
    display: block;
    margin: 150px auto 0px;
    background-color: #f6f6f6;
    text-align: center;
    position: relative;
    padding-bottom: 150px;
}
.instagram-section-container {
    max-width: 1410px;
    width: 92%;
    margin: 0px auto;
    text-align: center;
    display: block;
    padding: 125px 0px 130px;
}
.heading-align {
    display: -webkit-flex;
    align-items: center;
    justify-content: flex-start;
}

.blog-bg-flex-container {
    margin-bottom: 160px;
}

.meddmax-about-info::after {
    display: none;
}

@media (min-width: 300px) {
    .header-banner-section .seprate-contrent-text {
        padding: 0 10px;
    }

    .meddmax-imagery-section {
        max-height: 300px;
    }
}
