.contact-info-fluid-section
{
	width: 100%;
	display: block;
	margin: 0px auto;
	position: relative;
}
.contact-info-fluid-section::after
{
	content: "";
	position: absolute;
	right: 0;
	top: 0;
	width: 30%;
	display: inline-block;
	background-color: #f7f7f7;
}
.contact-container-form-section
{
	max-width: 1410px;
	width: 92%;
	display: flex;
	display: -webkit-flex;
	align-items: center;
	justify-content: space-between;
	margin: 140px auto 40px;
}
.contact-left-info-section
{
	width: 34.8%;
	display: inline-block;
	margin: 0px;
}
.contact-left-info-section h3
{
	
    font-family: 'proxima_novaextrabold';
    font-size: 52px;
    color: #0265a0;
    display: block;
    margin: 0px;
}
.contact-left-info-section hr
{
	
    border-top: 1px solid #000000;
    width: 54px;
    display: block;
    margin: 20px 0px 32px;
}
.contact-left-info-section p
{
	
   font-family: 'proxima_novaregular';
    font-size: 23px;
    color: #000000;
    display: block;
}
.contact-short-info-sec
{
	width: 100%;
	display: block;
	margin: 50px 0px 0px;
}
.each-short-info
{
	width: 100%;
	display: flex;
	display: -webkit-flex;
	align-items: flex-start;
	margin-bottom: 40px;
}
.contact-short-info-sec .each-short-info:last-child
{
	margin-bottom: 0px;
}
.each-short-info span
{
	display: flex;
    display: -webkit-flex;
    align-items: center;
    width: 30px;
    text-align: center;
}
.each-short-info i
{
	color: #0265a0;
    display: block;
	margin: 0px auto;
    font-size: 32px;
}
.each-short-info hr
{
	border-top: 0;
    width: 1px;
    height: 80px;
    margin: 0px 20px 0px 15px;
    border-right: 1px solid #0265a0;
	position: relative;
}
.each-short-info hr::before
{
	content: "";
	position: absolute;
	left: 0;
	right: 0;
	margin: 0px auto;
	width: 3px;
	height: 35px;
	background-color: #0265a0;
}
.each-short-info p
{
	font-family: 'proxima_novaregular';
    font-size: 18px;
    color: #000000;
    display: block;
}
.each-short-info p a
{
	color: #000000;
}
ul.contact-info-social-media
{
	display: flex;
	display: -webkit-flex;
	align-items: center;
	margin-top: 50px;
}
ul.contact-info-social-media li
{
	display: inline-block;
}
ul.contact-info-social-media li a
{
	position: relative;
    display: inline-block;
    font-family: 'proxima_novabold';
    font-size: 18px;
    color: #0265a0;
    line-height: 15px;
    height: 15px;
    padding: 0px 20px;
}
ul.contact-info-social-media li a::after
{
	content: "";
	position: absolute;
	right: 0;
	width: 2px;
	height: 100%;
	background-color: #0265a0;
}
ul.contact-info-social-media li:first-child a
{
	padding: 0px 20px 0px 0px;
}
.contact-right-form-section
{
	width: 54%;
    display: inline-block;
    margin: 0px;
    vertical-align: top;
}
.contact-right-form-section .form-group
{
	width: 100%;
	display: flex;
	display: -webkit-flex;
	align-items: center;
	justify-content: space-between;
}
.contact-right-form-section .form-group .form-control
{
	width: 48%;
	border-radius: 50px;
	background-color: #ffffff;
	font-family: 'proxima_novabold';
    font-size: 16px;
    color: #000000;
	padding: 20px 20px;
	display: inline-block;
	margin-bottom: 28px;
	border: 0px;
	box-shadow: 0px 8px 30px rgba(0,0,0,.08);
}
.contact-right-form-section .form-group .form-control:focus
{
	box-shadow: 0px 8px 30px rgba(0,0,0,.08) !important;
}
.contact-right-form-section .form-group .form-control.textarea
{
	width: 100%;
	border-radius: 25px;
}
.contact-right-form-section .form-group .form-control.textarea:focus
{
	outline: 0px !important;
	box-shadow: 0px 8px 30px rgba(0,0,0,.08);
}






