.sidenav {
  background: #ffffff;
  box-shadow: 0px 12px 52px rgb(0, 0, 10%);
  display: block;
  height: 100%;
  left: 0;
  position: fixed;
  overflow: auto;
  transform: translate(-460px, 0);
  transition: transform 0.3s;
  top: 0;
  width: 460px;
  z-index: 100;
  margin: 0px;
}

.sidenav.show {
  transform: translate(0, 0);
}

.sidenav-menu {
