}

.menu-link-call .sidenav-menu .dropdown-menu ul li a:hover {
  background-color: transparent;
  color: #017eb8;
  text-decoration: underline;
}

.menu-link-call .sidenav-menu .dropdown-menu ul li::after {
  content: none;
  position: static;
