.sidenav-overlay {
  background: rgba(0, 0, 0, 0.6);
  display: none;
  height: 100%;
  left: 0;
  position: fixed;
  top: 0;
  width: 100%;
  z-index: 99;
}

.sidenav-no-scroll {
  overflow: hidden !important;
}

.main-menu-link {
  width: 100%;
  display: block;
  background-color: #fff;
  border-top: 1px solid #000;
  border-top-color: rgba(0, 0, 0, 0.5);
}

.menu-link-call {
  width: 100%;
  display: flex;
  display: -webkit-flex;
  display: -moz-flex;
  align-items: center;
  justify-content: center;
}

.main-menu-link .snd-right-search-section {
  display: none;
}

.menu-link-call .sidenav {
  width: 100%;
  position: static;
  transform: inherit;
  box-shadow: unset;
  display: inline-block;
  height: inherit;
  overflow: inherit;
  transition: inherit;
  z-index: auto;
  background-color: transparent;
  text-align: center;
}

.menu-link-call .sidenav .sidenav-menu {
  display: flex;
  display: -webkit-flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
}

.menu-link-call .sidenav-menu li.dropdown {
  position: static;
}

.menu-link-call .sidenav-menu li a {
  display: inline-block;
  position: relative;
}

.menu-link-call .sidenav-menu li a::after {
  content: "";
  position: absolute;
  right: 0px;
  width: 1px;
  height: 16px;
  background-color: rgba(255, 255, 255, 0.24);
  top: 21px;
}

.menu-link-call .sidenav-menu li:last-child::after {
  content: inherit;
  position: static;
}

.menu-link-call .sidenav-menu li a {
  font-family: "Roboto Flex";
  font-size: 19px;
  color: #ffffff;
  display: inline-block;
  padding: 18px 33px;
  height: auto;
  line-height: normal;
  width: auto;
}

.menu-link-call .sidenav-menu li a:hover,
.menu-link-call .sidenav-menu li a:active,
.menu-link-call .sidenav-menu li a:focus {
  color: #ffffff;
}

.menu-link-call .sidenav-menu .sidenav-dropdown {
  position: absolute;
  left: 0;
  top: 100%;
  background-color: #fff;
  padding: 0px 0px;
  border-radius: 0px;
  box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.05);
}

.menu-link-call .sidenav-menu .sidenav-dropdown li:first-child a {
  padding: 8px 20px;
}

.menu-link-call .sidenav-menu .sidenav-dropdown li a {
  border-right: none;
  padding: 8px 20px;
}

.menu-link-call .sidenav-menu .sidenav-dropdown li:after {
  content: inherit;
  position: static;
  background-color: transparent;
  top: 20px;
}

