.cta-button-function {
  display: inline-block;
  background-color: #02b8f7;
  border: 1px solid #02b8f7;
  text-align: center;
  font-family: "Roboto Flex";
  font-size: 20px;
  color: #ffffff;
  border-radius: 50px;
  padding: 18px 60px;
  position: relative;
  cursor: pointer;
  -webkit-transition: all 0.3s ease-in-out;
  -moz-transition: all 0.3s ease-in-out;
  transition: all 0.3s ease-in-out;
}

.cta-button-function::after {
  content: "";
  position: absolute;
  right: -10px;
  bottom: -10px;
  width: 100%;
  height: 100%;
  border: 1px solid #02b8f7;
  border-radius: 50px;
  -webkit-transition: all 0.3s ease-in-out;
  -moz-transition: all 0.3s ease-in-out;
  transition: all 0.3s ease-in-out;
}

.cta-button-function:hover::after,
.cta-button-function:active::after,
.cta-button-function:focus::after {
