ul.customcheckbox-category li label {
  display: block;
  position: relative;
  font-size: 16px;
  padding: 0px 0px 0px 48px;
  z-index: 9;
  cursor: pointer;
  margin: 0px;
  -webkit-transition: all 0.25s linear;
}

ul.customcheckbox-category li .check {
  display: block;
  position: absolute;
  background-color: #ffffff;
  border-radius: 3px;
  height: 16px;
  width: 17px;
  top: 3px;
  left: 20px;
  z-index: 5;
  transition: border 0.25s linear;
  -webkit-transition: border 0.25s linear;
  -webkit-box-shadow: 0px 4px 4px rgb(0, 0, 32%);
  box-shadow: 0px 4px 4px rgb(0, 0, 32%);
}

ul.customcheckbox-category li .check::before {
  display: block;
  position: absolute;
  content: "";
  border-radius: 3px;
  height: 100%;
  width: 100%;
  top: 0;
  left: 0;
  margin: auto;
  transition: background 0.25s linear;
  -webkit-transition: background 0.25s linear;
}

input[type="radio"]:checked~.check::before {
  content: "\f00c";
  font-family: FontAwesome;
