html {
  height: 100%;
}

body {
  margin: 0;
  padding: 0;
  font-family: "Roboto Flex", sans-serif;
  font-size: 16px;
  overflow-x: hidden;
}

h1,
h2,
h3,
h4,
h5,
h6,
p,
a,
button,
select,
textarea {
  margin: 0;
  padding: 0;
  list-style: none;
  outline: none;
  -webkit-transition: all 0.3s ease-in-out;
  -moz-transition: all 0.3s ease-in-out;
  transition: all 0.3s ease-in-out;
}

ul,
ol,
li {
  margin: 0px;
  padding: 0;
  list-style: none;
  outline: none;
}
a {
  text-decoration: none;
}
a:hover,
a:active,
a:focus,
button:hover,
button:active,
button:focus {
  text-decoration: none;
  outline: none;
  -webkit-transition: all 0.3s ease-in-out;
  -moz-transition: all 0.3s ease-in-out;
  transition: all 0.3s ease-in-out;
}

input:focus,
select:focus {
  box-shadow: inherit !important;
}

input.form-control::-webkit-input-placeholder {
  color: #000000;
}

input.form-control::-moz-placeholder {
  color: #000000;
}

input.form-control:-ms-input-placeholder {
  color: #000000;
}

