.custom-container {
  max-width: 1600px;
  width: 92%;
  margin: 0px auto;
  display: block;
}

/*---Search Bar---*/
/*---Search Bar---*/

#search {
  position: fixed;
  top: 0px;
  left: 0px;
  width: 100%;
  height: 100%;
  background-color: rgba(255, 255, 255, 1);
  -webkit-transition: all 0.5s ease-in-out;
  -moz-transition: all 0.5s ease-in-out;
  -o-transition: all 0.5s ease-in-out;
  -ms-transition: all 0.5s ease-in-out;
  transition: all 0.5s ease-in-out;
  -webkit-transform: scale(0, 0);
  -moz-transform: scale(0, 0);
  -o-transform: scale(0, 0);
  -ms-transform: scale(0, 0);
  transform: scale(0, 0);
  opacity: 0;
  z-index: 100;
}

#search form {
  display: flex;
  display: -webkit-flex;
  align-items: center;
  justify-content: center;
}

.icon-search {
  display: none;
}

#search.open {
  -webkit-transform: scale(1, 1);
  -moz-transform: scale(1, 1);
  -o-transform: scale(1, 1);
  -ms-transform: scale(1, 1);
  transform: scale(1, 1);
  opacity: 1;
}

button.close {
  position: fixed;
  top: 0px;
  right: 10px;
  color: #000000;
  opacity: 1;
  padding: 0px;
  font-weight: 100;
}

button.close:hover {
  color: #f1ede3;
}

input[type="search"] {
  position: absolute;
  top: 50%;
  width: 92%;
  color: #000000;
  background: transparent;
  font-size: 20px;
  text-align: center;
  border: 0px;
  margin: 0px auto;
  margin-top: -50px;
  padding: 10px 0px;
  outline: none;
  border-bottom: 1px solid #d5d5d5;
  font-family: "Roboto Flex";
}

.btn {
  position: absolute;
  top: 50%;
  left: 0;
  right: 0;
  margin: 30px auto;
  border: 0px;
  display: block;
  padding: 13px 52px 12px;
  border-radius: 50px;
  background-color: #03a7e0;
  font-size: 20px;
  color: #ffffff;
  font-family: "Roboto Flex";
  font-weight: normal;
  width: 92%;
  outline: none;
  box-shadow: none;
}

.btn:hover,
.btn:active,
.btn:focus {
  color: #ffffff;
  background-color: #006ba5;
  border: 0px;
  outline: none;
  box-shadow: none;
}

.close {
  position: fixed;
  top: 15px;
  right: 15px;
  color: #fff;
  background-color: limegreen;
  border-color: green;
  opacity: 1;
  padding: 10px 17px;
  font-size: 52px;
  font-weight: 100;
}

.open {
  -webkit-transform: translate(0px, 0px) scale(1, 1);
  -moz-transform: translate(0px, 0px) scale(1, 1);
  -o-transform: translate(0px, 0px) scale(1, 1);
  -ms-transform: translate(0px, 0px) scale(1, 1);
  transform: translate(0px, 0px) scale(1, 1);
  opacity: 1;
}

.top-blue-row {
