}

.sidenav-header-top-section {
  width: 100%;
  display: flex;
  display: -webkit-flex;
  align-items: center;
  justify-content: space-between;
  background: -webkit-linear-gradient(135deg, #0174ae 0, #03a4dd 100%);
  background: -moz-linear-gradient(135deg, #0174ae 0, #03a4dd 100%);
  background: linear-gradient(135deg, #0174ae 0, #03a4dd 100%);
  padding: 28px 20px;
}

.sidenav-header-top-section div {
  display: flex;
  display: -webkit-flex;
  align-items: center;
  text-align: center;
}

.sidenav-header-top-section div a {
