}

.sidenav {
  background: #ffffff;
  box-shadow: 0px 12px 52px rgb(0, 0, 10%);
  display: block;
  height: 100%;
  left: 0;
  position: fixed;
  overflow: auto;
  transform: translate(-460px, 0);
  transition: transform 0.3s;
  top: 0;
  width: 460px;
  z-index: 100;
  margin: 0px;
}

.sidenav.show {
  transform: translate(0, 0);
}

.sidenav-menu {
  height: 100%;
  display: block;
}

.sidenav-menu li {
  font-size: 16px;
  font-weight: 400;
}

.sidenav-menu li {
  display: block;
}

.sidenav-menu li a {
  color: #ffffff;
  font-size: 19px;
  display: block;
  padding: 12px 30px;
  font-family: "Roboto Flex";
}

.sidenav-menu li a i {
  padding-right: 8px;
}

.sidenav-menu a:hover,
.sidenav-menu a.active,
.sidenav-menu a:focus {
  color: #00659f;
}

@media (min-width: 992px) {
  li.dropdown:hover .dropdown-menu {
    opacity: 1;
    visibility: visible;
    -webkit-transition: all 0.3s ease-in-out;
    -moz-transition: all 0.3s ease-in-out;
    transition: all 0.3s ease-in-out;
    -webkit-transform: translateY(0px);
    -moz-transform: translateY(0px);
    transform: translateY(0px);
    background: #eee;
  }
}
.dropdown-menu {
  left: 0;
  right: 0;
  margin: 0px auto;
  padding: 40px 60px 0px;
  box-shadow: 0px 20px 30px rgba(0, 0, 0, 0.2);
  border-radius: 0px;
  z-index: 50;
  border: 0px;
  min-width: inherit;
  display: block;
  opacity: 0;
  visibility: hidden;
  -webkit-transition: all 0.3s ease-in-out;
  -moz-transition: all 0.3s ease-in-out;
  transition: all 0.3s ease-in-out;
  -webkit-transform: translateY(50px);
  -moz-transform: translateY(50px);
  transform: translateY(50px);
}
.menu-link-call .sidenav-menu li.simple-drop {
  position: relative !important;
  .dropdown-menu.simple-dropdown {
    max-width: 400px !important;
