  border-right: none;
  border-right-style: none;
}

.dropdown-menu .dropdown-container {
  max-width: 1410px;
  width: 100%;
  margin: 0px auto;
  display: block;
  padding: 0px 30px;
}

.dropdown-menu div {
  display: inline-block;
  vertical-align: top;
  width: 24%;
  margin-bottom: 40px;
}

.dropdown-menu div h2 {
  font-family: "Roboto Flex";
  font-size: 19px;
  color: #017eb8;
  display: block;
  width: 100%;
  margin-bottom: 14px;
}

.dropdown-menu ul {
  display: block;
  vertical-align: top;
}

.dropdown-menu ul li {
  width: 100%;
  display: block;
  margin-bottom: 10px;
}

.menu-link-call .sidenav-menu .dropdown-menu ul li a {
  color: #000000;
  font-size: 16px;
  text-transform: capitalize;
  width: 100%;
  display: block;
  margin: 0px;
  padding: 0;
}

.menu-link-call .sidenav-menu .dropdown-menu ul li a::after {
  content: none;
  position: static;
}

.menu-link-call .sidenav-menu .dropdown-menu ul li a:hover {
  background-color: transparent;
  color: #017eb8;
  text-decoration: underline;
}

.menu-link-call .sidenav-menu .dropdown-menu ul li::after {
  content: none;
  position: static;
}

.sidenav-overlay {
  background: rgba(0, 0, 0, 0.6);
  display: none;
  height: 100%;
  left: 0;
  position: fixed;
  top: 0;
  width: 100%;
  z-index: 99;
}

.sidenav-no-scroll {
  overflow: hidden !important;
}

.main-menu-link {
  width: 100%;
  display: block;
  background-color: #fff;
  border-top: 1px solid #000;
  border-top-color: rgba(0, 0, 0, 0.5);
}

.menu-link-call {
  width: 100%;
  display: flex;
  display: -webkit-flex;
  display: -moz-flex;
  align-items: center;
  justify-content: center;
}

.main-menu-link .snd-right-search-section {
  display: none;
}

.menu-link-call .sidenav {
  width: 100%;
  position: static;
  transform: inherit;
  box-shadow: unset;
  display: inline-block;
  height: inherit;
  overflow: inherit;
  transition: inherit;
  z-index: auto;
  background-color: transparent;
  text-align: center;
}

.menu-link-call .sidenav .sidenav-menu {
  display: flex;
  display: -webkit-flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
}

.menu-link-call .sidenav-menu li.dropdown {
  position: static;
}

.menu-link-call .sidenav-menu li a {
  display: inline-block;
  position: relative;
}

.menu-link-call .sidenav-menu li a::after {
  content: "";
  position: absolute;
  right: 0px;
  width: 1px;
  height: 16px;
  background-color: rgba(255, 255, 255, 0.24);
  top: 21px;
}
.menu-link-call .sidenav-menu li:last-child {
  a:after {
    display: none;
  }
}
.menu-link-call .sidenav-menu li:last-child::after {
  content: inherit;
  position: static;
}

.menu-link-call .sidenav-menu li a {
  font-family: "Roboto Flex";
  font-size: 19px;
  color: #ffffff;
  display: inline-block;
  padding: 18px 33px;
  height: auto;
  line-height: normal;
  width: auto;
}

.menu-link-call .sidenav-menu li a:hover,
.menu-link-call .sidenav-menu li a:active,
.menu-link-call .sidenav-menu li a:focus {
  color: #ffffff;
}

.menu-link-call .sidenav-menu .sidenav-dropdown {
  position: absolute;
  left: 0;
  top: 100%;
  background-color: #fff;
  padding: 0px 0px;
  border-radius: 0px;
  box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.05);
}

.menu-link-call .sidenav-menu .sidenav-dropdown li:first-child a {
  padding: 8px 20px;
}

.menu-link-call .sidenav-menu .sidenav-dropdown li a {
  border-right: none;
  padding: 8px 20px;
}

.menu-link-call .sidenav-menu .sidenav-dropdown li:after {
  content: inherit;
  position: static;
  background-color: transparent;
  top: 20px;
}

.menu-link-call a.red-call-btn {
  background-color: #d4020c;
  border-radius: 100px;
  font-size: 16px;
  color: #fff;
  font-weight: 400;
  display: inline-block;
  padding: 5px 15px 4px;
}

.menu-link-call a.red-call-btn b {
  font-weight: 700;
}
