}

.main-banner-content-area {
  width: 100%;
  display: flex;
  display: -webkit-flex;
  align-items: center;
  justify-content: space-between;
  margin: 0px auto;
  position: relative;
}

.seprate-contrent-text {
  max-width: 730px;
  width: 100%;
  display: inline-block;
  margin: 0px;
  text-align: left;
}

.seprate-contrent-text h1 {
  font-family: "Roboto Flex";
  font-size: 80px;
  color: #ffffff;
  display: block;
  margin-bottom: 20px;
}

.seprate-contrent-text h3 {
  font-family: "Roboto Flex";
  font-size: 53px;
  color: #ffffff;
  display: block;
  margin: 0px;
}

.seprate-contrent-text hr {
  border-top: 1px solid #ffffff;
  width: 62px;
  display: block;
  margin: 30px 0px 43px;
}

.seprate-contrent-text ul {
  display: block;
  width: 100%;
  margin: 0px 0px 56px 0px;
}

.seprate-contrent-text ul li {
  font-family: "Roboto Flex";
  font-size: 26px;
  color: #ffffff;
  display: block;
  width: 100%;
  margin-bottom: 16px;
  position: relative;
  padding-left: 42px;
}

.seprate-contrent-text ul li::before {
  content: "";
  position: absolute;
