    font-weight: 600;
    padding: 8px 8px 8px 15px;
    width: 60%;
    position: relative;
    text-transform: uppercase;
    width: 80%;
    &::after {
      right: 0;
      border-left-width: 1.1em;
      border-right-color: transparent;

      content: "";
      position: absolute;
      display: block;
      bottom: 0;
      border: 23px solid #02b8f7;
      border-right-color: #f1f7fb;
      border-left-width: 20px;
      z-index: 0;
    }
    &:before {
      content: " ";
      position: absolute;
      background-image: url(../img/small-ribbon-icon.png);
      left: 0px;
      right: auto;
      background-size: cover;
      background-repeat: no-repeat;
      background-position: center;
      width: 10px;
      height: 11px;
      top: 46px;
    }
  }
